pm.test("判断返回结果中data的数组个数", function () {
  var jsonData = pm.response.json();
  var assert_value = pm.variables.get('assert_data_len');
  pm.expect(jsonData.data).to.have.lengthOf(assert_value);
});

pm.test("小于等于90天时判断是否有-", function () {
    var jsonData = pm.response.json();
    var assert_value = Number(pm.variables.get('assert_data_len'));
    console.log(typeof assert_value);
    if (assert_value <= 8) {
        pm.expect(jsonData.data[0][assert_value + 2]).to.equal("-");
    }
    else if (assert_value > 8 && assert_value <= 16) {
        pm.expect(jsonData.data[0][11]).to.equal("-");
    }
    else if (assert_value > 16 && assert_value <= 31) {
        pm.expect(jsonData.data[0][12]).to.equal("-");
    }
    else if (assert_value > 31 && assert_value <= 61) {
        pm.expect(jsonData.data[0][13]).to.equal("-");
    }
    else if (assert_value > 61 && assert_value <= 91) {
        pm.expect(jsonData.data[0][14]).to.equal("-");
    }
});
